mapper.sql:
FROM tb_ccg cg
where 1=1

	and cg.reg_date between #{sh_start_date} and #{sh_end_date}
	and cg.mod_date between #{sh_start_date} and #{sh_end_date}



	and cg.code_group_name LIKE CONCAT('%',#{sh_val},'%')
	and cg.code_en_name LIKE CONCAT('%', #{sh_val} ,'%')
	and cg.use_ny = #{sh_use_ny}
	and cg.use_ny = #{sh_use_ny}

mapper.insert:

	SELECT last_insert_id()

insert into tb_ccg(
	code_group_name
	,code_en_name
    ,use_ny
    ,reg_date
)
values(
	#{code_group_name}
	,#{code_en_name}
	,#{use_ny}
	,now()
)

mapper.update:
UPDATE tb_ccg SET 
	code_group_name = #{code_group_name}
    ,code_en_name = #{code_en_name}
    ,use_ny = #{use_ny}
    ,mod_date = now()
WHERE 1=1
	and seq = #{seq}
