mapper.select:
select c.seq ,c.ccg_seq ,(select cg.code_group_name from tb_ccg cg where 1=1 and c.ccg_seq = cg.seq) code_group_name ,c.seq ,c.seq ,c.code_name ,c.code_name_en ,c.use_ny ,c.sort ,c.reg_date ,c.mod_date from tb_cc c left join tb_ccg on c.ccg_seq = tb_ccg.seq where 1=1 and c.reg_date between #{sh_start_date} and #{sh_end_date} and c.mod_date between #{sh_start_date} and #{sh_end_date}
and c.seq = #{sh_val} and c.ccg_seq = #{sh_val} and tb_ccg.code_group_name LIKE CONCAT('%',#{sh_val},'%') and c.seq = #{sh_val} and c.seq = #{sh_val} and c.code_name LIKE CONCAT('%',#{sh_val},'%') and c.code_name_en LIKE CONCAT('%',#{sh_val},'%') and c.sort = #{sh_val}
and c.use_ny = 0 and c.use_ny = 1
