mapper.sql:
FROM tb_member
where 1=1

	 and reg_date between #{sh_start_date} and #{sh_end_date}
	 and mod_date between #{sh_start_date} and #{sh_end_date}
	 and del_date between #{sh_start_date} and #{sh_end_date}
	 and dob between #{sh_start_date} and #{sh_end_date}



	and seq = #{sh_val}
	and name LIKE CONCAT('%',#{sh_val},'%')
	and id LIKE CONCAT('%',#{sh_val},'%')
	and gender LIKE CONCAT('%',#{sh_val},'%')
	and address LIKE CONCAT('%',#{sh_val},'%')
	and tell LIKE CONCAT('%',#{sh_val},'%')
	and email LIKE CONCAT('%',#{sh_val},'%')



	and admin_ny = 0
	and admin_ny = 1
